        driver_class=#{updateDO.driverClass}
        name=#{updateDO.name}
        password=#{updateDO.password}
        url=#{updateDO.url}
        username=#{updateDO.username}
        type=#{updateDO.type}
        initial_size=#{updateDO.initialSize}
        max_active=#{updateDO.maxActive}
        min_idle=#{updateDO.minIdle}
        gmt_modified=CURRENT_TIMESTAMP
 where is_deleted=0

   and  ${propertyName}=#{values.[${index}]}

